const defaultState = {
    cash: 5,
  }

export const cashReduser = (state = defaultState, action) => {
    switch (action.type) {
      case "ADD_CASH":
        debugger
        return {...state, cash: state.cash + action.payload}
      case "GET_CASH":
        return {...state, cash: state.cash - action.payload}
      default:
        return state;
    }
  }
