import logo from './logo.svg';
import './App.css';
import { useDispatch, useSelector } from 'react-redux';

function App() {
  const dispatch = useDispatch();
  const cash = useSelector(state => state.cash);

  const addCash = () => {
    const amount = Number(prompt("Введіть суму для поповнення:"));
    if (!isNaN(amount)) {
      dispatch({ type: "ADD_CASH", payload: amount });
    }
  }

  const getCash = () => {
    const amount = Number(prompt("Введіть суму для зняття:"));
    if (!isNaN(amount)) {
      dispatch({ type: "GET_CASH", payload: amount });
    }
  }



  return (
    <div className="App">
      <div style={{fontSize:"3rem"}}>{cash}</div>
      <div style={{display:"flex"}}>
        <button onClick={addCash}>Пополнить счет</button>
        <button onClick={getCash}>Снять со счета</button>

      </div>

    </div>
  );
}

export default App;
