defoultStore = {
    castomers: [],
}

export const customerReduser = (state = defaultState, action) => {
    switch (action.type) {
      case "ADD_Customer":
        debugger
        return {...state, cash: state.cash + action.payload}
      case "GET_Customers":
        return {...state, cash: state.cash - action.payload}
      default:
        return state;
    }
  }
