import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import { createStore } from 'redux'; // Изменено на createStore
import { Provider } from 'react-redux'; // Изменено на Provider



const defaultState = {
  cash: 5,
}

const reduser = (state = defaultState, action) => {
  switch (action.type) {
    case "ADD_CASH":
      debugger
      return {...state, cash: state.cash + action.payload}
    case "GET_CASH":
      return {...state, cash: state.cash - action.payload}
    default:
      return state;
  }
}

const CustomerReduser = (state = defaultState, action) => {
  switch (action.type) {
    case "ADD_Customer":
      debugger
      return {...state, cash: state.cash + action.payload}
    case "GET_Customers":
      return {...state, cash: state.cash - action.payload}
    default:
      return state;
  }
}

const store = createStore(reduser);

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <Provider store={store}>
      <App /> 
    </Provider>
  </React.StrictMode>
);

reportWebVitals();
