const defaultState = {
    castomers: [],
}

export const customerReduser = (state = defaultState, action) => {
    switch (action.type) {
      case "ADD_Customer":
        debugger
        return {...state, customers: [...state.customers, action.payload]}
      case "REMOVE_Customers":
        return {...state, customers: state.customers.filter(customer => customers.id !== action.payload)}
      default:
        return state;
    }
  }
